import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';

const languages = [
  { id: 'python', label: 'Python', icon: 'üêç' },
  { id: 'javascript', label: 'JavaScript', icon: 'JS' },
  { id: 'java', label: 'Java', icon: '‚òï' },
  { id: 'cpp', label: 'C++', icon: '‚öôÔ∏è' },
  { id: 'c', label: 'C', icon: 'üîß' }
];

const DebugPage = () => {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [useExternalApi, setUseExternalApi] = useState(false);
  const { currentUser } = useAuth();

  const handleDebug = async () => {
    try {
      // Implementation will be added
      console.log('Debugging code:', { code, language, useExternalApi });
    } catch (error) {
      console.error('Error debugging code:', error);
    }
  };

  return (
    <div className="min-h-screen bg-[#1E1E1E] text-white p-4">
      <div className="max-w-6xl mx-auto">
        {/* Language Selection */}
        <div className="flex justify-center mb-4 bg-[#2D2D2D] rounded-lg p-2">
          {languages.map((lang) => (
            <button
              key={lang.id}
              onClick={() => setLanguage(lang.id)}
              className={`px-4 py-2 rounded-lg flex items-center space-x-2 ${
                language === lang.id 
                  ? 'bg-[#4F46E5] text-white' 
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              <span className="text-xl">{lang.icon}</span>
              <span>{lang.label}</span>
            </button>
          ))}
        </div>

        {/* API Toggle */}
        <div className="flex justify-center mb-6">
          <div className="bg-[#2D2D2D] rounded-full px-4 py-2 flex items-center space-x-2">
            <span>Use External API</span>
            <button
              onClick={() => setUseExternalApi(!useExternalApi)}
              className={`w-12 h-6 rounded-full p-1 transition-colors duration-200 ease-in-out ${
                useExternalApi ? 'bg-[#4F46E5]' : 'bg-gray-600'
              }`}
            >
              <div
                className={`w-4 h-4 bg-white rounded-full transform transition-transform duration-200 ease-in-out ${
                  useExternalApi ? 'translate-x-6' : 'translate-x-0'
                }`}
              />
            </button>
            <span className="text-gray-400">‚ÑπÔ∏è</span>
          </div>
        </div>

        {/* Code Editor */}
        <div className="bg-[#1E1E1E] rounded-lg overflow-hidden border border-gray-700">
          <div className="bg-[#2D2D2D] px-4 py-2 flex items-center">
            <span className="text-sm text-gray-400">Syntax validation only</span>
            <div className="ml-auto flex space-x-2">
              <div className="w-3 h-3 rounded-full bg-red-500" />
              <div className="w-3 h-3 rounded-full bg-yellow-500" />
              <div className="w-3 h-3 rounded-full bg-green-500" />
            </div>
          </div>
          <textarea
            value={code}
            onChange={(e) => setCode(e.target.value)}
            className="w-full h-[500px] bg-[#1E1E1E] p-4 font-mono text-white focus:outline-none"
            placeholder="Paste your code here..."
            spellCheck="false"
          />
        </div>

        {/* Debug Button */}
        <div className="mt-4 flex justify-end">
          <button
            onClick={handleDebug}
            className="bg-[#4F46E5] text-white px-6 py-2 rounded-lg hover:bg-[#4338CA] focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#4F46E5]"
          >
            Debug Code
          </button>
        </div>
      </div>
    </div>
  );
};

export default DebugPage;
