import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import HomePage from './pages/HomePage';
import HistoryPage from './pages/HistoryPage';
import DebugPage from './pages/DebugPage';
import ProfileSetup from './pages/ProfileSetup';
import './App.css';

const LoginPage = () => {
  const { signInWithGoogle } = useAuth();
  const navigate = useNavigate();
  
  const handleLogin = async () => {
    try {
      const user = await signInWithGoogle();
      if (user) {
        navigate('/profile-setup');
      }
    } catch (error) {
      console.error('Login failed:', error);
    }
  };
  
  return (
    <div className="min-h-screen bg-[#4F46E5] flex items-center justify-center p-4">
      <div className="bg-white rounded-lg shadow-xl p-8 max-w-md w-full">
        <img 
          src="/logo.png" 
          alt="AI Code Debugger" 
          className="w-16 h-16 mx-auto mb-4"
        />
        
        <h1 className="text-4xl font-bold text-center text-[#4F46E5] mb-2">
          AI-Powered Code Debugger
        </h1>
        
        <p className="text-gray-600 text-center mb-8">
          Debug and enhance your code with AI
        </p>

        <div className="space-y-6 mb-8">
          <div className="flex items-center">
            <div className="flex-shrink-0 text-[#4F46E5] text-2xl">‚ö°</div>
            <div className="ml-4">
              <h3 className="text-lg font-medium">Instant Debugging</h3>
              <p className="text-gray-500">Get immediate feedback on code errors</p>
            </div>
          </div>

          <div className="flex items-center">
            <div className="flex-shrink-0 text-[#4F46E5] text-2xl">üîç</div>
            <div className="ml-4">
              <h3 className="text-lg font-medium">Smart Suggestions</h3>
              <p className="text-gray-500">Receive AI-powered code improvement tips</p>
            </div>
          </div>

          <div className="flex items-center">
            <div className="flex-shrink-0 text-[#4F46E5] text-2xl">üåê</div>
            <div className="ml-4">
              <h3 className="text-lg font-medium">Multiple Languages</h3>
              <p className="text-gray-500">Support for Python, JavaScript, Java, C and C++</p>
            </div>
          </div>
        </div>

        <button
          onClick={handleLogin}
          className="w-full flex items-center justify-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-[#4F46E5]"
        >
          <img src="/google-icon.png" alt="Google" className="w-5 h-5 mr-2" />
          Sign in with Google
        </button>
      </div>
    </div>
  );
};

// Protected route component
const ProtectedRoute = ({ children }) => {
  const { currentUser, loading } = useAuth();
  
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-[#4F46E5]">
        <div className="text-white">Loading...</div>
      </div>
    );
  }
  
  if (!currentUser) {
    return <Navigate to="/login" />;
  }
  
  return children;
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/profile-setup" element={<ProtectedRoute><ProfileSetup /></ProtectedRoute>} />
          <Route path="/debug" element={<ProtectedRoute><DebugPage /></ProtectedRoute>} />
          <Route path="/history" element={<ProtectedRoute><HistoryPage /></ProtectedRoute>} />
          <Route path="/app" element={<ProtectedRoute><HomePage /></ProtectedRoute>} />
          <Route path="/" element={<Navigate to="/app" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
